select.if:
select *
from (
select row_number() over() as rownum,
IndexingResult.* from
IndexingResult
where collectionId = #{collectionId}
) as tmp where rownum >
#{start} and rownum <= #{end}

mapper.insert:
insert into IndexingResult (
collectionId, type, status, docSize, insertSize, updateSize, deleteSize,
isScheduled,
startTime, endTime, duration)
values (
#{collectionId}, #{type}, #{status}, #{docSize}, #{insertSize}
,
#{updateSize}, #{deleteSize}, #{isScheduled}, #{startTime},
#{endTime}, #{duration}
)
