select.if:
select *
from (
select row_number() over() as rownum, TaskHistory.* from
TaskHistory
) as tmp where rownum > #{start} and rownum <= #{end}

mapper.insert:
insert into TaskHistory ( 
jobId, jobClassName, args, isSuccess, resultStr, isScheduled, startTime, endTime, duration
)
values (
#{jobId}, #{jobClassName}, #{args}, #{isSuccess}, #{resultStr}, #{isScheduled}, #{startTime}, #{endTime}, #{duration}
